import { useEffect } from "react";
import { loadComments } from "../../store/product.action.js";
import { CommentAdd } from "./product-add.jsx";
import { CommentFilter } from "./product-filter.jsx";
import { CommentList } from "./product-list.jsx";
import { useSelector } from 'react-redux'

export function CommentIndex() {

    // const  filterBy = 8
        // const products  = []
    const { filterBy, products } = useSelector((storeState) => storeState.productModule)

    useEffect(() => {
        onLoadComments(filterBy)
    }, [filterBy])


    async function onLoadComments(filterBy) {
        try {
            await loadComments(filterBy)
            // console.log('products:', products)
        } catch(err) {
            console.log('err:', err)
        }
    }

    return (
        <section className='product-index'>
            <CommentAdd />
            <CommentFilter />
            {products && <CommentList products={products} />}
        </section>
    )

}
